mapper.select:
SELECT p.id, p.order_id, p.external_id, p.channel, p.amount, p.currency, p.status, p.request_payload, p.response_payload, p.notify_payload, p.last_polled_at, p.last_notified_at, p.created_at, p.updated_at, o.order_no AS orderNo FROM payment_order p JOIN orders o ON o.id = p.order_id
AND o.order_no = #{orderNo}
AND p.external_id = #{externalId}
AND p.channel = #{channel}
AND p.status = #{status}
AND p.created_at >= #{createdFrom}
AND p.created_at <= #{createdTo}
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1)
FROM payment_order p
JOIN orders o ON o.id = p.order_id


        AND o.order_no = #{orderNo}


        AND p.external_id = #{externalId}


        AND p.channel = #{channel}


        AND p.status = #{status}


        AND p.created_at  >=  #{createdFrom}


        AND p.created_at  <=  #{createdTo}
