mapper.select:
SELECT cart_item.id, cart_item.sku_id, cart_item.quantity, cart_item.selected, cart_item.added_at, sku.product_id, COALESCE(pi18n.title, p.title) AS title, p.cover_image_url, #{currency} AS currency,
CASE WHEN price.is_active = 1 AND price.sale_price IS NOT NULL THEN price.sale_price WHEN price.is_active = 1 THEN price.list_price ELSE NULL END AS unit_price
NULL AS unit_price
FROM shopping_cart_item cart_item JOIN product_sku sku ON sku.id = cart_item.sku_id JOIN product p ON p.id = sku.product_id
LEFT JOIN product_i18n pi18n ON pi18n.product_id = p.id AND pi18n.locale = #{locale}
LEFT JOIN product_i18n pi18n ON 1 = 0
LEFT JOIN product_price price ON price.sku_id = sku.id AND price.currency = #{currency}
WHERE cart_item.user_id = #{userId} ORDER BY cart_item.added_at DESC LIMIT #{limit} OFFSET #{offset}
